import React, { useState, useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';
import io from 'socket.io-client';

const socket = io('http://localhost:5000'); // Assuming the server is on localhost:5000

const ChatBox = () => {
    const [message, setMessage] = useState('');
    const [chat, setChat] = useState([]);

    useEffect(() => {
        socket.on('message', ({ user, message }) => {
            setChat([...chat, { user, message }]);
        });
    }, [chat]);

    const sendMessage = (e) => {
        e.preventDefault();
        socket.emit('message', { user: 'User1', message });
        setMessage('');
    };

    return (
        <div>
            <div style={{ height: '200px', overflowY: 'scroll', border: '1px solid #ccc' }}>
                {chat.map((entry, idx) => (
                    <div key={idx}><strong>{entry.user}: </strong>{entry.message}</div>
                ))}
            </div>
            <Form onSubmit={sendMessage}>
                <Form.Control
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message"
                />
                <Button type="submit" variant="primary" className="mt-2">
                    Send
                </Button>
            </Form>
        </div>
    );
};

export default ChatBox;
